/**
 * Briddge theme default functions
 */
add_action('after_switch_theme', 'briddge_theme_default_setup_options');
function briddge_theme_default_setup_options(){
	$default_options = briddge_get_default_options();
	update_option( 'briddge_options', json_decode( $default_options, true ) );
}

/**
* Enqueue Google Web Fonts.
*/
function briddge_theme_default_fonts_url() {
   $font_url = '';
   
   /*
   Translators: If there are characters in your language that are not supported
   by chosen font(s), translate this to 'off'. Do not translate into your own language.
	*/
	$font_url = add_query_arg( 'family', urlencode( 'Varela Round|Playfair Display&subset=latin' ), "//fonts.googleapis.com/css" );

   return $font_url;
}

